$(document).ready(function (){

// declare variables
// random number generated by computer
var targetNum = 0;

// user score 
var userNum = 0;

// 4 crystals with hidden value that resets start of the game
var emerald = 0;
var ruby = 0;
var diamond = 5;
var sapphire = 0;

// score counter: Wins & Losses
var winCounter = 0;
var lossesCounter = 0;



$("#winCounter").text(winCounter);
$("#lossesCounter").text(lossesCounter);



// new game function
function newGame() {
  // alert new game
  alert("New Game")


  // generate random target number between 19 and 120
  targetNum = Math.floor(Math.random()*40) + 19;

  // write on page
  $("#targetNum").text(targetNum);

  // generate and assign random value to each crystal
  emerald = Math.floor(Math.random()*12) + 1;
  ruby = Math.floor(Math.random()*12) + 1;
  diamond = Math.floor(Math.random()*12) + 1;
  sapphire = Math.floor(Math.random()*12) + 1;

  // tie random values to crystal buttons
  $("#diamond").attr("value", diamond)
  $("#emerald").attr("value", emerald)
  $("#ruby").attr("value", ruby)
  $("#sapphire").attr("value", sapphire)

  // empty #userNum
  $("#userNum").empty();
  userNum = 0;

  
}

// tie newGame to a button
$("#newGame").on("click", function (){
  newGame();
  console.log(diamond)
  console.log(emerald)
  console.log(ruby)
  console.log(sapphire)
})


// button click event for crystal buttons
$(".crystal").on("click", function (){
  // value of total added by the value of crystals
  userNum += parseInt($(this).attr("value"));
  console.log(userNum)

  // write on page
  $("#userNum").text(userNum)

  // Win scenario
  if (targetNum === userNum) {
  winCounter++;
  alert("WIN")
  console.log("winCounter: " + winCounter);
  $("#winCounter").text(winCounter);
  //loss scenario
  }
  else if (targetNum < userNum) {
    lossesCounter++;
    alert("Lose")
    console.log("lossesCounter: " + lossesCounter);
    $("#lossesCounter").text(lossesCounter);
    newGame();
  }
})
































//document.ready
})